import React, { useEffect, useState } from 'react'
import jwt from 'jsonwebtoken'
import { useNavigate } from 'react-router-dom'
import './styles/for_dash.css';


const Dashboard = () => {
    const navigate = useNavigate()
    const [quote, setQuote] = useState('')
    const [tempQuote, setTempQuote] = useState('')
    const [userInfo, setUserInfo] = useState({ username: '', email: '' })
    async function populateQuote() {
        const req = await fetch('http://localhost:1337/api/quote', {
            headers: {
                'X-access-token': localStorage.getItem('token'),
            },
        })

        const data = await req.json()
        if(data.status === 'ok') {
            setQuote(data.quote)
        } else {
            alert(data.error)
        }
    }

    useEffect(() => {
        const token = localStorage.getItem('token')
        if(token) {
            const user = jwt.decode(token)
            if(!user) {
                localStorage.removeItem('token')
                navigate('/login')
            } else {
                setUserInfo({ username: user.Name, email: user.email })
                populateQuote()
            }
        }
    }, [])

    function navLogout() {
        localStorage.removeItem('token')
        navigate('/login')
    }

    async function updateQuote() {
        const req = await fetch('http://localhost:1337/api/quote', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'X-access-token': localStorage.getItem('token'),
            },
            body: JSON.stringify({
                quote: tempQuote,
            }),

        })

        const data = await req.json()
        if(data.status === 'ok') {
            setQuote(tempQuote)
            setTempQuote('')
        } else {
            alert(data.error)
        }
    }

    return (
        <div class="form-container">
        <div class="rectangle">

        </div>
        <h1>Welcome, {userInfo.username || 'User'}</h1>
        <h2>Email: {userInfo.email || 'Email not available'}</h2>
        <br />
        <h1>your quote: {quote || 'no quote found'}</h1>
        <form onSubmit={updateQuote}>
            <input type ='text' placeholder='Quote' value={tempQuote} onChange={e => setTempQuote(e.target.value)} />
            <input type='submit' value='Update quote' />
        </form>
        <br />
        <button onClick={navLogout}>Logout</button>
    </div>
        )
}

export default Dashboard
